mapper.update:

	update member
	set uPw=#{uPw}, uName=#{uName}, uEmail=#{uEmail}, gender=#{gender}, uBirthday=#{uBirthday}, 
		uZipcode=#{uZipcode}, uAddr=#{uAddr}, uHobby=#{uHobby}, uJob=#{uJob}
	where uId = #{uId}


	update tblBoard
	set depth=depth+1
	where ref=#{ref} and depth>=#{depth}


	update tblboard
	set pos=pos+1
	where ref=#{ref} and pos>=#{pos}


	update tblBoard 
	set subject=#{subject}, content=#{content}
	where num= #{num}

mapper.select:
select * from tblBoard
where #{keyField} like CONCAT('%',#{keyWord},'%' )
order by ref desc, pos asc LIMIT #{pageStart}, #{perPageNum}
